"use client"

import { DiffViewer } from "@/registry/new-york-v4/ai-elements/diff-viewer"

export default function DiffViewerExpanded() {
  const oldContent = `import React from "react";

export function calculateTotal(items) {
  let total = 0;
  for (let i = 0; i < items.length; i++) {
    total = total + items[i].price * items[i].quantity;
  }
  return total;
}

export function formatCurrency(amount) {
  return "$" + amount.toFixed(2);
}

export function applyDiscount(total, discount) {
  return total - discount;
}`

  const newContent = `import React from "react";

export interface Item {
  price: number;
  quantity: number;
}

export function calculateTotal(items: Item[]): number {
  return items.reduce((total, item) => {
    return total + item.price * item.quantity;
  }, 0);
}

export function formatCurrency(amount: number): string {
  return new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD',
  }).format(amount);
}

export function applyDiscount(total: number, discountPercentage: number): number {
  return total * (1 - discountPercentage / 100);
}`

  return (
    <DiffViewer
      oldContent={oldContent}
      newContent={newContent}
      language="typescript"
      initialLinesToShow={8}
      isExpandable={true}
    />
  )
}
