"use client"

import { DiffViewer } from "@/registry/new-york-v4/ai-elements/diff-viewer"

export default function DiffViewerNewFile() {
  const oldContent = ""

  const newContent = `export interface User {
  id: string;
  name: string;
  email: string;
  createdAt: Date;
}

export function createUser(data: Omit<User, 'id' | 'createdAt'>): User {
  return {
    id: crypto.randomUUID(),
    ...data,
    createdAt: new Date(),
  };
}`

  return (
    <DiffViewer
      oldContent={oldContent}
      newContent={newContent}
      language="typescript"
    />
  )
}
