"use client"

import { DiffViewer } from "@/registry/new-york-v4/ai-elements/diff-viewer"

export default function DiffViewerDemo() {
  const oldContent = `function greet(name) {
  console.log("Hello " + name);
}`

  const newContent = `function greet(name: string) {
  console.log(\`Hello, \${name}!\`);
  return \`Hello, \${name}!\`;
}`

  return <DiffViewer oldContent={oldContent} newContent={newContent} />
}
