"use client"

import { DiffViewer } from "@/registry/new-york-v4/ai-elements/diff-viewer"

export default function DiffViewerLanguages() {
  const oldContent = `def calculate_sum(numbers):
    total = 0
    for num in numbers:
        total = total + num
    return total

result = calculate_sum([1, 2, 3, 4, 5])
print(result)`

  const newContent = `from typing import List

def calculate_sum(numbers: List[int]) -> int:
    """Calculate the sum of a list of numbers."""
    return sum(numbers)

def main() -> None:
    result = calculate_sum([1, 2, 3, 4, 5])
    print(f"The sum is: {result}")

if __name__ == "__main__":
    main()`

  return (
    <DiffViewer
      oldContent={oldContent}
      newContent={newContent}
      language="python"
      initialLinesToShow={15}
      isExpandable={false}
    />
  )
}
